import React, {useState} from 'react'
import {Route, Switch, Redirect} from 'react-router-dom'
import ColorIndex from './ColorIndex'
import Color from './Color'
import AddColorForm from './AddColorFrom'

const Routes = () => {
	const INITIAL_STATE = {
		red: "#FF0000",
	}
	
	const [colors, setColors] = useState(INITIAL_STATE)
	const addColor = (newColor) => setColors(previousItems => ({...previousItems, ...newColor}))
	console.log(colors)


	// function renderCurrentColor(props) {
  //   const { color } = props.match.params
  //   const hex = colors[color]
	// 	console.log('hex', hex)
	// 	console.log('color', color)
  //   return <Color {...props} hex={hex} color={color} />
  // }

	return (
		<Switch>
			<Route exact path='/colors/new'>
				<AddColorForm addColor={addColor}/>
			</Route>
			<Route exact path='/colors/:color'>
				<Color />
			</Route>
			<Route exact path='/colors'>
				<ColorIndex render={renderCurrentColor}/>
			</Route>
			<Redirect to='/colors' />
		</Switch>
	)
}

export default Routes
